package com.java.practice;

public class JavaSwitchExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int choice = 100;
		switch (choice) {
		case 1:
			System.out.println("1");
		case 10:
			System.out.println("10");
		default:
			System.out.println("default");
		}
	}

}
